INSERT INTO novel VALUES
(NULL, "The Last of Us: A Post-Apocalyptic Journey", "In a world ravaged by a deadly fungal infection, survivors Joel and Ellie embark on a perilous cross-country journey to find a cure.", "the-last-of-us-a-post-apocalyptic-journey", 29, 'published', NOW(), NULL),
(NULL, "Uncharted: Lost Treasures", "Follow treasure hunter Nathan Drake as he unravels ancient mysteries and races against ruthless enemies in search of hidden treasures.", "uncharted-lost-treasures", 24, 'published', NOW(), NULL),
(NULL, "God of War: Revenge of the Spartan", "Join Kratos, the Spartan warrior, on a quest for redemption as he battles mythical creatures and challenges the gods themselves.", "god-of-war-revenge-of-the-spartan", 35, 'published', NOW(), NULL),
(NULL, "Dark Souls: The Age of Fire", "Enter a dark and treacherous world where players must face unforgiving challenges, powerful bosses, and uncover the secrets of the Age of Fire.", "dark-souls-the-age-of-fire", 27, 'published', NOW(), NULL),
(NULL, "Sekiro: Shadows of Vengeance", "Become the one-armed wolf, a shinobi warrior, as you embark on a mission of revenge in feudal Japan filled with intense sword fights and supernatural foes.", "sekiro-shadows-of-vengeance", 31, 'published', NOW(), NULL),
(NULL, "Red Dead Redemption: Outlaw's Redemption", "Set in the wild west, outlaw Arthur Morgan's loyalty is tested as he confronts rival gangs, lawmen, and confronts his own inner demons.", "red-dead-redemption-outlaws-redemption", 28, 'published', NOW(), NULL),
(NULL, "Fullmetal Alchemist Brotherhood: Alchemy's Legacy", "Follow the Elric brothers, Edward and Alphonse, as they search for the Philosopher's Stone to restore their bodies in a world where alchemy rules.", "fullmetal-alchemist-brotherhood-alchemys-legacy", 26, 'published', NOW(), NULL),
(NULL, "Hunter x Hunter: The Pursuit of Dreams", "Gon Freecss embarks on a perilous journey to become a Hunter, encounter powerful adversaries, and uncover the secrets of his father's disappearance.", "hunter-x-hunter-the-pursuit-of-dreams", 29, 'published', NOW(), NULL),
(NULL, "Fire Force: Inferno's Brigade", "In a world where spontaneous human combustion turns people into living infernos, a special firefighting force battles against supernatural threats.", "fire-force-infernos-brigade", 25, 'published', NOW(), NULL),
(NULL, "My Hero Academia: Rise of Heroes", "In a society where most of the population possesses superpowers, young Izuku Midoriya strives to become the greatest hero and protect the innocent.", "my-hero-academia-rise-of-heroes", 28, 'published', NOW(), NULL),
(NULL, "Magi: The Labyrinth of Magic", "Follow the adventures of Alibaba, Aladdin, and Morgiana as they navigate a world of magic, treachery, and political intrigue.", "magi-the-labyrinth-of-magic", 24, 'published', NOW(), NULL),
(NULL, "Ao Haru Ride: A Tale of Youthful Love", "Futaba Yoshioka's life takes a turn when she reconnects with her middle school crush, Kou Tanaka, and they navigate the complexities of young love.", "ao-haru-ride-a-tale-of-youthful-love", 23, 'published', NOW(), NULL),
(NULL, "Gintama: Samurai Comedy Chronicles", "Join Gintoki Sakata, a lazy samurai, and his eccentric friends as they take on odd jobs and hilarious adventures in an alternate Edo period.", "gintama-samurai-comedy-chronicles", 26, 'published', NOW(), NULL),
(NULL, "Vagabond: The Way of the Sword", "Based on the life of legendary swordsman Miyamoto Musashi, witness his journey of self-discovery and mastery of the sword.", "vagabond-the-way-of-the-sword", 33, 'published', NOW(), NULL),
(NULL, "One-Punch Man: The Hero for Fun", "Saitama, a superhero with unrivaled strength, seeks to find opponents who can provide him a real challenge in a world overrun by monsters.", "one-punch-man-the-hero-for-fun", 28, 'published', NOW(), NULL),
(NULL, "Attack on Titan: Walls of Desperation", "In a world where humanity is on the brink of extinction, follow Eren Yeager and his friends as they battle colossal titans and uncover the secrets behind their existence.", "attack-on-titan-walls-of-desperation", 26, 'published', NOW(), NULL),
(NULL, "Death Note: The Power of the Death Note", "When a high school student named Light Yagami discovers a mysterious notebook with the power to kill anyone whose name is written in it, he embarks on a quest to create a new world free from criminals.", "death-note-the-power-of-the-death-note", 22, 'published', NOW(), NULL),
(NULL, "Naruto: The Path of the Ninja", "Follow Naruto Uzumaki, a young ninja with a dream of becoming Hokage, as he trains, battles powerful enemies, and learns the value of friendship.", "naruto-the-path-of-the-ninja", 27, 'published', NOW(), NULL),
(NULL, "Bleach: Soul Reaper Chronicles", "Ichigo Kurosaki, a teenager with the ability to see ghosts, becomes a Soul Reaper and protects the living world from malevolent spirits and otherworldly threats.", "bleach-soul-reaper-chronicles", 25, 'published', NOW(), NULL),
(NULL, "One Piece: Pirate's Legacy", "Join Monkey D. Luffy and his crew, the Straw Hat Pirates, as they search for the ultimate treasure, the One Piece, and navigate the treacherous world of pirates.", "one-piece-pirates-legacy", 29, 'published', NOW(), NULL),
(NULL, "Harry Potter and the Sorcerer's Stone", "Harry Potter, an orphaned wizard, discovers he is a famous wizard and enrolls in the Hogwarts School of Witchcraft and Wizardry, where he uncovers dark secrets and battles the dark wizard Lord Voldemort.", "harry-potter-and-the-sorcerers-stone", 20, 'published', NOW(), NULL),
(NULL, "The Lord of the Rings: The Fellowship of the Ring", "Frodo Baggins embarks on a perilous journey to destroy the One Ring and save Middle-earth from the evil forces of Sauron, accompanied by a fellowship of diverse characters.", "the-lord-of-the-rings-the-fellowship-of-the-ring", 22, 'published', NOW(), NULL),
(NULL, "Pride and Prejudice", "Elizabeth Bennet navigates societal expectations and her own prejudices as she develops an unlikely love story with the wealthy and proud Mr. Darcy.", "pride-and-prejudice", 18, 'published', NOW(), NULL),
(NULL, "To Kill a Mockingbird", "Scout Finch recounts her experiences growing up in a racially divided Southern town and her father's courageous defense of an innocent black man accused of rape.", "to-kill-a-mockingbird", 18, 'published', NOW(), NULL),
(NULL, "1984", "In a totalitarian society ruled by Big Brother, Winston Smith rebels against the oppressive regime and falls in love with Julia, leading to dire consequences.", "1984", 17, 'published', NOW(), NULL),
(NULL, "The Great Gatsby", "Jay Gatsby, a wealthy and enigmatic millionaire, pursues his lost love, Daisy Buchanan, in a tale of decadence, illusion, and the corruption of the American Dream.", "the-great-gatsby", 16, 'published', NOW(), NULL),
(NULL, "The Hobbit", "Bilbo Baggins, a reluctant hobbit, embarks on an epic adventure to help a group of dwarves reclaim their homeland from the fearsome dragon Smaug.", "the-hobbit", 16, 'published', NOW(), NULL),
(NULL, "The Chronicles of Narnia: The Lion, the Witch, and the Wardrobe", "Four siblings stumble upon a magical wardrobe that transports them to the enchanting land of Narnia, where they join forces with the lion Aslan to defeat the White Witch and restore peace.", "the-chronicles-of-narnia-the-lion-the-witch-and-the-wardrobe", 22, 'published', NOW(), NULL),
(NULL, "Alice's Adventures in Wonderland", "Alice follows a white rabbit down a rabbit hole and enters a whimsical and nonsensical world filled with eccentric characters and peculiar adventures.", "alices-adventures-in-wonderland", 19, 'published', NOW(), NULL),
(NULL, "The Catcher in the Rye", "Holden Caulfield recounts his experiences and struggles as a disenchanted teenager in New York City, questioning the superficiality and phoniness of society.", "the-catcher-in-the-rye", 17, 'published', NOW(), NULL),
(NULL, "The Hunger Games: Arena of Survival", "In a dystopian future, Katniss Everdeen volunteers for the Hunger Games, a televised fight to the death, to protect her sister and challenge the oppressive Capitol.", "the-hunger-games-arena-of-survival", 21, 'published', NOW(), NULL),
(NULL, "The Da Vinci Code", "Renowned symbologist Robert Langdon and cryptologist Sophie Neveu unravel a series of clues and secrets hidden in the works of Leonardo da Vinci, leading to a shocking discovery.", "the-da-vinci-code", 18, 'published', NOW(), NULL),
(NULL, "The Girl with the Dragon Tattoo", "Investigative journalist Mikael Blomkvist and hacker Lisbeth Salander delve into a dark and twisted mystery involving murder, corruption, and family secrets.", "the-girl-with-the-dragon-tattoo", 20, 'published', NOW(), NULL),
(NULL, "Gone Girl", "When Amy Dunne goes missing, suspicion falls on her husband Nick as secrets and lies unravel in this psychological thriller that keeps readers guessing until the shocking truth is revealed.", "gone-girl", 17, 'published', NOW(), NULL),
(NULL, "The Fault in Our Stars", "Hazel Grace Lancaster and Augustus Waters, two teenagers battling cancer, embark on a poignant and heartbreaking journey of love, hope, and mortality.", "the-fault-in-our-stars", 16, 'published', NOW(), NULL),
(NULL, "The Maze Runner", "Thomas wakes up in a mysterious maze with no memory of who he is. As he and a group of other teenagers navigate the deadly maze, they uncover dark secrets about their existence.", "the-maze-runner", 19, 'published', NOW(), NULL),
(NULL, "The Alchemist", "Follow Santiago, a young shepherd, as he embarks on a journey to discover his personal legend and find his true purpose in life, encountering magical and philosophical insights along the way.", "the-alchemist", 15, 'published', NOW(), NULL),
(NULL, "The Shining", "Jack Torrance, an aspiring writer, takes a job as the winter caretaker of the isolated Overlook Hotel. As the hotel's dark past resurfaces, Jack's sanity begins to unravel, posing a threat to his family.", "the-shining", 18, 'published', NOW(), NULL),
(NULL, "The Chronicles of Narnia: The Magician's Nephew", "Digory and Polly stumble upon magical rings that transport them to other worlds, leading them on a series of adventures that culminate in the creation of Narnia.", "the-chronicles-of-narnia-the-magicians-nephew", 20, 'published', NOW(), NULL),
(NULL, "Ender's Game", "Child prodigy Ender Wiggin is recruited by the military to train in a battle school to prepare for an imminent alien invasion. As Ender's skills are put to the test, he faces moral dilemmas and the true nature of war.", "enders-game", 17, 'published', NOW(), NULL),
(NULL, "The Giver", "Jonas lives in a seemingly perfect society where pain and suffering have been eliminated. However, as he begins to receive memories of the past, he discovers the dark secrets behind their idyllic existence.", "the-giver", 16, 'published', NOW(), NULL),
(NULL, "The Girl on the Train", "Rachel, an alcoholic and divorcee, becomes embroiled in a missing person case as she obsessively observes a couple from the train. Her involvement unravels a web of deceit, betrayal, and murder.", "the-girl-on-the-train", 19, 'published', NOW(), NULL),
(NULL, "The Picture of Dorian Gray", "Dorian Gray, a young man, remains eternally youthful while a portrait of him ages and reflects the corrupting effects of his immoral actions and hedonistic lifestyle.", "the-picture-of-dorian-gray", 18, 'published', NOW(), NULL),
(NULL, "The Kite Runner", "Amir, a young boy from Afghanistan, confronts his past and seeks redemption as he navigates the complexities of friendship, betrayal, and guilt in the tumultuous backdrop of his war-torn homeland.", "the-kite-runner", 20, 'published', NOW(), NULL),
(NULL, "Percy Jackson and the Lightning Thief", "Percy Jackson, a demigod and son of Poseidon, embarks on a quest to prevent a war among the gods after being accused of stealing Zeus' lightning bolt.", "percy-jackson-and-the-lightning-thief", 16, 'published', NOW(), NULL);
